    <div className="home">
      <div className="workout">
        {workouts &&
          workouts.map((workout) => (
            <WorkoutDetails
              key={workout._id} // Key ensures each component is uniquely identified
              workout={workout}
            ></WorkoutDetails>
          ))}
      </div>
      <WorkoutForm /> {/* Form to add new workout */}
    </div>
  );
}

export default Home;
