import { useState } from "react";
import { useAuthContext } from "../hooks/useAuthContext";
import axios from "axios";
import { useWorkoutContext } from "../hooks/useWorkoutContext";
import { ThreeDots } from "react-loader-spinner"; // Import the spinner
import { toast, ToastContainer } from "react-toastify"; // Import toast
import "react-toastify/dist/ReactToastify.css"; // Import styles

export const WorkoutForm = () => {
  const { user } = useAuthContext();
  const { dispatch } = useWorkoutContext();

  const [title, setTitle] = useState("");
  const [load, setLoad] = useState("");
  const [reps, setReps] = useState("");
  const [error, setError] = useState(null);
  const [emptyField, setEmptyField] = useState([]);
  const [loading, setLoading] = useState(false); // New loading state

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true); // Start loading
    setError(null);

    try {
      const res = await axios.post(
        "https://gym-blog-3.onrender.com/api/workouts/",
        {
          title,
          load,
          reps,
        },
        {
          headers: { Authorization: `Bearer ${user.token}` },
        }
      );

      dispatch({ type: "CREATE_WORKOUT", payload: res.data });
      setTitle("");
      setLoad("");
      setReps("");
      setError(null);
      setEmptyField([]);

      // // Show success toast
      // toast.success("Workout added successfully!");
    } catch (err) {
      if (err.response && err.response.data) {
        setError(err.response.data.error);
        setEmptyField(err.response.data.emptyFields);
      } else {
        setError("Something went wrong, please try again");
      }
    } finally {
      setLoading(false); // Stop loading
    }
  };

  return (
    <>
      <form className="create" onSubmit={handleSubmit}>
        <h3>Add a new workout</h3>
        <label>Exercise Title:</label>
        <input
          placeholder="title"
          value={title}
          type="text"
          className={emptyField.includes("title") ? "error" : ""}
          onChange={(e) => setTitle(e.target.value)}
        />
        <label>Load:</label>
        <input
          placeholder="load"
          value={load}
          type="number"
          className={emptyField.includes("load") ? "error" : ""}
          onChange={(e) => setLoad(e.target.value)}
        />
        <label>Reps:</label>
        <input
          placeholder="reps"
          value={reps}
          type="number"
          className={emptyField.includes("reps") ? "error" : ""}
          onChange={(e) => setReps(e.target.value)}
        />
        <button disabled={loading} type="submit">
          {loading ? (
            <ThreeDots color="#fff" height={10} width={30} /> // Spinner in the button
          ) : (
            "Add Workout"
          )}
        </button>
        {error && <div className="error">{error}</div>}
      </form>
      <ToastContainer
        position="top-center"
        autoClose={3000}
        hideProgressBar={false}
        closeOnClick={true}
        rtl={false}
        pauseOnHover={false}
        draggable={false}
      />
    </>
  );
};
